// Binance Account Data Fetcher - Exact replica of working bash script
const crypto = require('crypto');

// Check environment variables
if (!$env.BINANCE_API_KEY || !$env.BINANCE_SECRET_KEY) {
  throw new Error('BINANCE_API_KEY and BINANCE_SECRET_KEY environment variables must be set');
}

// Set credentials (from environment)
const API_KEY = $env.BINANCE_API_KEY;
const API_SECRET = $env.BINANCE_SECRET_KEY;

// Generate fresh timestamp (important!)
const TIMESTAMP = Date.now();
console.log('Timestamp:', TIMESTAMP);

// Create query string
const QUERY = `recvWindow=5000&timestamp=${TIMESTAMP}`;
console.log('Query:', QUERY);

// Generate signature (exact same as bash script)
const SIGNATURE = crypto.createHmac('sha256', API_SECRET)
  .update(QUERY)
  .digest('hex');
console.log('Signature:', SIGNATURE);

// Construct final URL
const finalUrl = `https://api.binance.com/api/v3/account?${QUERY}&signature=${SIGNATURE}`;
console.log('Final URL:', finalUrl);

// Make API call using Node.js built-in https module
const https = require('https');
const url = require('url');

try {
  const parsedUrl = url.parse(finalUrl);

  // Create promise-based HTTPS request
  const response = await new Promise((resolve, reject) => {
    const options = {
      hostname: parsedUrl.hostname,
      path: parsedUrl.path,
      method: 'GET',
      headers: {
        'X-MBX-APIKEY': API_KEY,
        'Content-Type': 'application/json'
      },
      timeout: 10000
    };

    const req = https.request(options, (res) => {
      let data = '';

      res.on('data', (chunk) => {
        data += chunk;
      });

      res.on('end', () => {
        try {
          const jsonData = JSON.parse(data);
          if (res.statusCode >= 200 && res.statusCode < 300) {
            resolve(jsonData);
          } else {
            reject(new Error(`HTTP ${res.statusCode}: ${JSON.stringify(jsonData)}`));
          }
        } catch (parseError) {
          reject(new Error(`Failed to parse response: ${data}`));
        }
      });
    });

    req.on('error', (error) => {
      reject(error);
    });

    req.on('timeout', () => {
      req.destroy();
      reject(new Error('Request timeout'));
    });

    req.end();
  });

  console.log('‚úÖ Binance API Success!');
  console.log('Account data received:', JSON.stringify(response, null, 2));

  return [{
    json: response
  }];

} catch (error) {
  console.error('‚ùå Binance API Error:', error.message);
  throw error;
}

