// Binance Account Data Fetcher - Exact replica of working bash script
const crypto = require('crypto');

// Check environment variables
if (!$env.BINANCE_API_KEY || !$env.BINANCE_SECRET_KEY) {
  throw new Error('BINANCE_API_KEY and BINANCE_SECRET_KEY environment variables must be set');
}

// Set credentials (from environment)
const API_KEY = $env.BINANCE_API_KEY;
const API_SECRET = $env.BINANCE_SECRET_KEY;

// Generate fresh timestamp (important!)
const TIMESTAMP = Date.now();
console.log('Timestamp:', TIMESTAMP);

// Create query string
const QUERY = `recvWindow=5000&timestamp=${TIMESTAMP}`;
console.log('Query:', QUERY);

// Generate signature (exact same as bash script)
const SIGNATURE = crypto.createHmac('sha256', API_SECRET)
  .update(QUERY)
  .digest('hex');
console.log('Signature:', SIGNATURE);

// Construct final URL
const finalUrl = `https://api.binance.com/api/v3/account?${QUERY}&signature=${SIGNATURE}`;
console.log('Final URL:', finalUrl);

// Make API call using Node.js built-in https module
const https = require('https');
const url = require('url');

try {
  const parsedUrl = url.parse(finalUrl);

  // Create promise-based HTTPS request
  const response = await new Promise((resolve, reject) => {
    const options = {
      hostname: parsedUrl.hostname,
      path: parsedUrl.path,
      method: 'GET',
      headers: {
        'X-MBX-APIKEY': API_KEY,
        'Content-Type': 'application/json'
      },
      timeout: 10000
    };

    const req = https.request(options, (res) => {
      let data = '';

      res.on('data', (chunk) => {
        data += chunk;
      });

      res.on('end', () => {
        try {
          const jsonData = JSON.parse(data);
          if (res.statusCode >= 200 && res.statusCode < 300) {
            resolve(jsonData);
          } else {
            reject(new Error(`HTTP ${res.statusCode}: ${JSON.stringify(jsonData)}`));
          }
        } catch (parseError) {
          reject(new Error(`Failed to parse response: ${data}`));
        }
      });
    });

    req.on('error', (error) => {
      reject(error);
    });

    req.on('timeout', () => {
      req.destroy();
      reject(new Error('Request timeout'));
    });

    req.end();
  });

  console.log('✅ Binance API Success!');
  console.log('Account data received:', JSON.stringify(response, null, 2));

  return [{
    json: response
  }];

} catch (error) {
  console.error('❌ Binance API Error:', error.message);
  throw error;
}

// EUR-based Risk Management Configuration - Embedded in Code Node
const RISK_CONFIG = {
  PORTFOLIO: {
    TARGET_BTC_ALLOCATION: 0.75,     // 75% BTC target
    REBALANCE_BAND: 0.05,            // ±5% rebalancing threshold
    SATELLITE_POOL_PERCENTAGE: 0.3,  // 30% for active trading
  },
  TRADING: {
    MAX_TRADES_PER_WEEK: 3,          // Maximum satellite trades per week
    SLIPPAGE_TOLERANCE: 0.01,        // 1% maximum slippage
    MIN_BTC_BALANCE: 0.01,           // Minimum BTC to maintain
    MIN_EUR_BALANCE: 5,              // Minimum EUR to maintain
  },
  CIRCUIT_BREAKERS: {
    MAX_PORTFOLIO_DECLINE_7D: -0.20, // Stop if portfolio down >20% in 7 days
    MAX_BTC_DECLINE_24H: -0.10,      // Stop if BTC down >10% in 24 hours
  },
  FEAR_GREED_RULES: {
    EXTREME_FEAR: { RANGE: [0, 20], PERCENTAGE: 7.5, DESCRIPTION: 'Extreme Fear - Aggressive Buy' },
    FEAR: { RANGE: [21, 30], PERCENTAGE: 4, DESCRIPTION: 'Fear - Moderate Buy' },
    NEUTRAL: { RANGE: [31, 60], PERCENTAGE: 1, DESCRIPTION: 'Neutral - DCA if below MA20' },
    GREED: { RANGE: [61, 70], PERCENTAGE: 4, DESCRIPTION: 'Greed - Small Sell' },
    HIGH_GREED: { RANGE: [71, 80], PERCENTAGE: 30, DESCRIPTION: 'High Greed - Moderate Sell' },
    EXTREME_GREED: { RANGE: [81, 100], PERCENTAGE: 10, DESCRIPTION: 'Extreme Greed - Aggressive Sell' }
  }
};

// Main Trading Logic
const fearGreedData = $node["Get Fear & Greed Index"].json;
const btcPriceData = $node["Get BTC Price"].json;
const ma20Data = $node["Get BTC 20-Day MA"].json;
const balanceData = $node["Get Binance Account Data"].json;

// Parse Fear & Greed Index
const fearGreedIndex = parseInt(fearGreedData.data[0].value);
const fearGreedValue = fearGreedData.data[0].value_classification;

// Parse BTC Price (now in EUR)
const btcPrice = parseFloat(btcPriceData.price);

// Calculate 20-day MA
const closes = ma20Data.map(candle => parseFloat(candle[4]));
const ma20 = closes.reduce((sum, close) => sum + close, 0) / closes.length;

// Parse Portfolio Balances (Spot API format) - Updated for EUR
const btcBalance = parseFloat(balanceData.balances.find(b => b.asset === 'BTC')?.free || 0);
const eurBalance = parseFloat(balanceData.balances.find(b => b.asset === 'EUR')?.free || 0);

// Debug log for troubleshooting
console.log('Balance data received:', JSON.stringify(balanceData));
console.log('BTC Balance:', btcBalance, 'EUR Balance:', eurBalance);
const totalValue = (btcBalance * btcPrice) + eurBalance;
const btcAllocation = (btcBalance * btcPrice) / totalValue;
const satellitePool = totalValue * RISK_CONFIG.PORTFOLIO.SATELLITE_POOL_PERCENTAGE;

// Trading Logic Based on Fear & Greed Index
let action = 'HOLD';
let tradePercentage = 0;
let tradeSizeEUR = 0;
let notes = '';

// Determine action based on Fear & Greed Index using config
if (fearGreedIndex >= RISK_CONFIG.FEAR_GREED_RULES.EXTREME_FEAR.RANGE[0] && fearGreedIndex <= RISK_CONFIG.FEAR_GREED_RULES.EXTREME_FEAR.RANGE[1]) {
  action = 'BUY';
  tradePercentage = RISK_CONFIG.FEAR_GREED_RULES.EXTREME_FEAR.PERCENTAGE;
  tradeSizeEUR = satellitePool * (tradePercentage / 100);
  notes = RISK_CONFIG.FEAR_GREED_RULES.EXTREME_FEAR.DESCRIPTION;
} else if (fearGreedIndex >= RISK_CONFIG.FEAR_GREED_RULES.FEAR.RANGE[0] && fearGreedIndex <= RISK_CONFIG.FEAR_GREED_RULES.FEAR.RANGE[1]) {
  action = 'BUY';
  tradePercentage = RISK_CONFIG.FEAR_GREED_RULES.FEAR.PERCENTAGE;
  tradeSizeEUR = satellitePool * (tradePercentage / 100);
  notes = RISK_CONFIG.FEAR_GREED_RULES.FEAR.DESCRIPTION;
} else if (fearGreedIndex >= RISK_CONFIG.FEAR_GREED_RULES.NEUTRAL.RANGE[0] && fearGreedIndex <= RISK_CONFIG.FEAR_GREED_RULES.NEUTRAL.RANGE[1]) {
  // Neutral - DCA if below MA20
  if (btcPrice < ma20) {
    action = 'BUY';
    tradePercentage = RISK_CONFIG.FEAR_GREED_RULES.NEUTRAL.PERCENTAGE;
    tradeSizeEUR = satellitePool * (tradePercentage / 100);
    notes = RISK_CONFIG.FEAR_GREED_RULES.NEUTRAL.DESCRIPTION;
  } else {
    action = 'HOLD';
    notes = 'Neutral - Hold (Price above MA20)';
  }
} else if (fearGreedIndex >= RISK_CONFIG.FEAR_GREED_RULES.GREED.RANGE[0] && fearGreedIndex <= RISK_CONFIG.FEAR_GREED_RULES.GREED.RANGE[1]) {
  action = 'SELL';
  tradePercentage = RISK_CONFIG.FEAR_GREED_RULES.GREED.PERCENTAGE;
  tradeSizeEUR = satellitePool * (tradePercentage / 100);
  notes = RISK_CONFIG.FEAR_GREED_RULES.GREED.DESCRIPTION;
} else if (fearGreedIndex >= RISK_CONFIG.FEAR_GREED_RULES.HIGH_GREED.RANGE[0] && fearGreedIndex <= RISK_CONFIG.FEAR_GREED_RULES.HIGH_GREED.RANGE[1]) {
  action = 'SELL';
  tradePercentage = RISK_CONFIG.FEAR_GREED_RULES.HIGH_GREED.PERCENTAGE;
  tradeSizeEUR = satellitePool * (tradePercentage / 100);
  notes = RISK_CONFIG.FEAR_GREED_RULES.HIGH_GREED.DESCRIPTION;
} else if (fearGreedIndex >= RISK_CONFIG.FEAR_GREED_RULES.EXTREME_GREED.RANGE[0] && fearGreedIndex <= RISK_CONFIG.FEAR_GREED_RULES.EXTREME_GREED.RANGE[1]) {
  action = 'SELL';
  tradePercentage = RISK_CONFIG.FEAR_GREED_RULES.EXTREME_GREED.PERCENTAGE;
  tradeSizeEUR = satellitePool * (tradePercentage / 100);
  notes = RISK_CONFIG.FEAR_GREED_RULES.EXTREME_GREED.DESCRIPTION;
}

// Risk Management Checks
const portfolioChange7d = $workflow.static?.data?.portfolioValue7dAgo ? 
  ((totalValue - $workflow.static?.data?.portfolioValue7dAgo) / $workflow.static?.data?.portfolioValue7dAgo) : 0;

const btcChange24h = $workflow.static?.data?.btcPrice24hAgo ? 
  ((btcPrice - $workflow.static?.data?.btcPrice24hAgo) / $workflow.static?.data?.btcPrice24hAgo) : 0;

// Circuit Breaker Logic using config
if (portfolioChange7d < RISK_CONFIG.CIRCUIT_BREAKERS.MAX_PORTFOLIO_DECLINE_7D) {
  action = 'HOLD';
  notes += ' - Circuit Breaker: Portfolio down >20% in 7 days';
}

if (btcChange24h < RISK_CONFIG.CIRCUIT_BREAKERS.MAX_BTC_DECLINE_24H) {
  action = 'HOLD';
  notes += ' - Circuit Breaker: BTC down >10% in 24h';
}

// Minimum Balance Checks using config (updated for EUR)
if (action === 'BUY' && eurBalance < Math.max(RISK_CONFIG.TRADING.MIN_EUR_BALANCE, tradeSizeEUR)) {
  action = 'HOLD';
  notes += ' - Insufficient EUR balance';
}

if (action === 'SELL' && btcBalance < RISK_CONFIG.TRADING.MIN_BTC_BALANCE) {
  action = 'HOLD';
  notes += ' - Insufficient BTC balance';
}

// Calculate trade size in BTC for sells
const tradeSizeBTC = action === 'SELL' ? tradeSizeEUR / btcPrice : 0;

// Prepare output (updated for EUR)
const result = {
  fearGreedIndex,
  fearGreedValue,
  btcPrice,
  ma20,
  btcBalance,
  eurBalance,
  totalValue,
  btcAllocation: btcAllocation * 100,
  satellitePool,
  action,
  tradePercentage,
  tradeSizeEUR,
  tradeSizeBTC,
  notes,
  timestamp: new Date().toISOString(),
  portfolioChange7d: portfolioChange7d * 100,
  btcChange24h: btcChange24h * 100
};

// Update static data for next run - FIXED: Proper initialization of nested objects
if (!$workflow.static) {
  $workflow.static = {};
}
if (!$workflow.static?.data) {
  $workflow.static = {data: {}}
}
$workflow.static = {data: {btcPrice24hAgo: btcPrice, portfolioValue7dAgo: totalValue}}

return [{ json: result }];

// Risk Management Configuration for Order Preparation
const RISK_CONFIG = {
  TRADING: {
    SLIPPAGE_TOLERANCE: 0.01,        // 1% maximum slippage
  }
};

// Generate Binance Order Parameters
const data = $json;
const btcPrice = data.btcPrice;

// Calculate limit price with configured slippage tolerance
const slippageTolerance = RISK_CONFIG.TRADING.SLIPPAGE_TOLERANCE;
let limitPrice;
let quantity;
let side;

if (data.action === 'BUY') {
  side = 'BUY';
  limitPrice = btcPrice * (1 + slippageTolerance); // Buy slightly above market
  quantity = (data.tradeSizeEUR / limitPrice).toFixed(8); // Use 8 decimal precision for BTC
} else if (data.action === 'SELL') {
  side = 'SELL';
  limitPrice = btcPrice * (1 - slippageTolerance); // Sell slightly below market
  quantity = data.tradeSizeBTC.toFixed(8); // Use 8 decimal precision for BTC
}

console.log('Order Calculation:', {
  action: data.action,
  tradeSizeEUR: data.tradeSizeEUR,
  btcPrice: btcPrice,
  limitPrice: limitPrice.toFixed(2),
  calculatedQuantity: quantity,
  estimatedValue: (parseFloat(quantity) * limitPrice).toFixed(2)
});

// Binance order parameters
const orderParams = {
  symbol: 'BTCEUR',
  side: side,
  type: 'LIMIT',
  timeInForce: 'GTC',
  quantity: quantity,
  price: limitPrice.toFixed(2),
  timestamp: Date.now()
};

// Generate query string for signature
const queryString = Object.keys(orderParams)
  .map(key => `${key}=${orderParams[key]}`)
  .join('&');

return [{
  json: {
    ...data,
    orderParams,
    queryString,
    limitPrice,
    quantity: parseFloat(quantity)
  }
}];

// Generate HMAC SHA256 signature for order
const crypto = require('crypto');

// Check if environment variables are set
if (!$env.BINANCE_SECRET_KEY) {
  throw new Error('BINANCE_SECRET_KEY environment variable not set');
}

const queryString = $json.queryString;
console.log('Order signature query string:', queryString);

const signature = crypto.createHmac('sha256', $env.BINANCE_SECRET_KEY)
  .update(queryString)
  .digest('hex');

console.log('Generated order signature:', signature);

return [{
  json: {
    ...$json,
    signature
  }
}];

