// Monthly Rebalancing Logic with Fear & Greed Integration
// Check if today is the 1st day of the month - only run rebalancing then
const today = new Date();
const isFirstDayOfMonth = today.getDate() === 1;

if (!isFirstDayOfMonth) {
  // Skip rebalancing if not the first day of the month
  console.log('Skipping rebalancing - not the first day of the month (Day', today.getDate(), ')');
  return [{
    json: {
      needsRebalancing: false,
      action: 'SKIP',
      notes: `Rebalancing skipped - not first day of month (Day ${today.getDate()})`,
      timestamp: new Date().toISOString()
    }
  }];
}

console.log('🗓️ First day of month - proceeding with monthly rebalancing');

const fearGreedData = $node["Get Fear & Greed Index"].json;
const balanceData = $node["Get Binance Account Data"].json;
const btcPriceData = $node["Get BTC Price"].json;

