  needsRebalancing = false;
  action = 'HOLD';
  notes += ' - Insufficient EUR for rebalancing';
}

return [{
  json: {
    needsRebalancing,
    action,
    tradeAmount,
    currentBtcAllocation: currentBtcAllocation * 100,
    targetBtcAllocation: targetBtcAllocation * 100,
    fearGreedIndex,
    fearGreedValue,
    fearGreedStrategy,
    btcPrice,
    btcBalance,
    eurBalance,
    totalValue,
    notes,
    timestamp: new Date().toISOString()
  }
}];

