  if (num === null || num === undefined || num === '' || isNaN(num)) return 'None';
  return parseFloat(num).toFixed(decimals);
};

const formatPercent = (num, decimals = 1) => {
  if (num === null || num === undefined || num === '' || isNaN(num)) return 'None';
  return parseFloat(num).toFixed(decimals);
};

