  // This is a trading decision (could be HOLD or trade intent)
  const statusEmoji = data.action === 'HOLD' ? '⏸️' : '🔄';
  const statusText = data.action === 'HOLD' ? 'No Trade Executed' : 'Trade Recommended';

  message = `${statusEmoji} *${statusText}*

